DROP TABLE cms_group;
CREATE TABLE cms_group ( ID int NOT NULL AUTO_INCREMENT, NAME varchar(50), DESCRIBTION varchar(100), PRIMARY KEY (ID) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE cms_role;
CREATE TABLE cms_role ( ID int NOT NULL AUTO_INCREMENT, NAME varchar(50), ROLE_TYPE varchar(50), PRIMARY KEY (ID) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE cms_user;
CREATE TABLE cms_user ( ID int NOT NULL AUTO_INCREMENT, NICKNAME varchar(30), USERNAME varchar(32), PASSWORD varchar(32), SEX int COMMENT '0未知1男2女', EMAIL varchar(50), MOBILE_PHONE varchar(30), STATUS int COMMENT '1启用 0停用', CREATE_DATE datetime, REMARK varchar(200), UPDATE_TIME timestamp DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, CREATE_AUTHOR int, PRIMARY KEY (ID) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE cms_user_group;
CREATE TABLE cms_user_group ( ID int NOT NULL AUTO_INCREMENT, USER_ID int, GROUP_ID int, PRIMARY KEY (ID) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE cms_user_role;
CREATE TABLE cms_user_role ( ID int NOT NULL AUTO_INCREMENT, USER_ID int, ROLE_ID int, PRIMARY KEY (ID) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
